CREATE TABLE customer_care (id INT NOT NULL, name VARCHAR(100) NOT NULL, profile LONGTEXT DEFAULT NULL COMMENT '(DC2Type:json)', email VARCHAR(100) DEFAULT NULL, first_name VARCHAR(100) DEFAULT NULL, last_name VARCHAR(100) DEFAULT NULL, deleted TINYINT(1) DEFAULT '0' NOT NULL, active TINYINT(1) DEFAULT '1' NOT NULL, INDEX customer_care_name (name), INDEX customer_care_email (email), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE action_history (id INT AUTO_INCREMENT NOT NULL, type VARCHAR(50) NOT NULL, description VARCHAR(255) NOT NULL, date DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, history LONGTEXT DEFAULT NULL COMMENT '(DC2Type:json)', user_name VARCHAR(50) NOT NULL, INDEX action_history_type (type), INDEX action_history_date (date), INDEX action_history_user_name (user_name), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE owner (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(100) DEFAULT NULL, surname VARCHAR(100) DEFAULT NULL, company VARCHAR(255) NOT NULL, email VARCHAR(100) NOT NULL, profile LONGTEXT DEFAULT NULL COMMENT '(DC2Type:json)', language VARCHAR(255) DEFAULT NULL, country VARCHAR(4) DEFAULT NULL, zip VARCHAR(20) DEFAULT NULL, address VARCHAR(255) DEFAULT NULL, city VARCHAR(255) DEFAULT NULL, deleted TINYINT(1) DEFAULT '0' NOT NULL, active TINYINT(1) DEFAULT '1' NOT NULL, county VARCHAR(120) DEFAULT NULL, INDEX owner_name (name), UNIQUE INDEX owner_uq_company (company), UNIQUE INDEX owner_uq_email (email), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE enc (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(255) NOT NULL, surname VARCHAR(255) DEFAULT NULL, form LONGTEXT DEFAULT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE user (id INT AUTO_INCREMENT NOT NULL, user VARCHAR(100) NOT NULL, type VARCHAR(30) NOT NULL, password VARCHAR(255) NOT NULL, active TINYINT(1) DEFAULT '1' NOT NULL, name VARCHAR(50) NOT NULL, owner_id INT DEFAULT NULL, deleted TINYINT(1) DEFAULT '0' NOT NULL, INDEX user_type (type), INDEX user_active (active), UNIQUE INDEX user_uq_user (user), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE user_login (id INT AUTO_INCREMENT NOT NULL, loginDate DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, ip_address VARCHAR(50) NOT NULL, user_id INT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
